package JavaW3School;

public class multipleParameter {
	
	static void myMethod(String fname, int age) {
		System.out.println(fname +"\t"+ "is"+"\t" + age);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		myMethod("Charu", 29);
		myMethod("Mohini", 28);
		myMethod("Ritika", 19);
		

	}

	
	
	
	
}
